import { Movie } from '../movie';
import { MovieService } from '../movie.service';
import { Component, OnInit, Input } from '@angular/core';
import { ActivatedRoute } from "@angular/router";


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.less']
})

export class HomeComponent implements OnInit {

  albums:any = [];


  constructor(private movieService: MovieService) { }


    ngOnInit() {
      this.albums = this.movieService.getMovies();
    }

  }
<div  *ngFor="let album of albums | async">{{albums.id}}Esta puta mierda va</div>



  <div class="container" >
    <div class="row" *ngIf="let movie of movies">
      <div
        class="col-4 col-md-4 col-lg-3 my-3 px-2 px-md-4"
        *ngfor="let album of movies"
      >
        <div>
          <a  
          *ngFor="let movie of movies" 
          class="view overlay zoom" 
          routerLink="movie/{{ movies.id }}">
            <img
              class="poster-img hoverable"
              src="https://image.tmdb.org/t/p/w400/{{ movies.poster_path }}"
              title="{{ movie.title }}"
              alt="{{ movie.title }}"
            />

            <h3>{{ movie.title }}</h3>
            <p>{{ movie.overview}}</p>
          </a>
        </div>
